# https://adventofcode.com/2017/day/19
mutable struct Grid
    data::Array
    x::Int64
    y::Int64
    direction::Int64
    letters::Array{Char}
    steps::Int64

    Grid(data::Array) = begin
        for i in 1:length(data[1])
            if data[1][i] == '|'
                return new(data, 1, i, 2, Char[], 0)
            end
        end
    end
end

Base.getindex(g::Grid, x::Int) = g.data[x]

function moved_from(g::Grid)
    d = g.direction % 4
    if d % 2 == 0
        return (g.x - d + 1, g.y)
    else
        return (g.x, g.y + d - 2)
    end
end

function move(g::Grid)
    d = g.direction % 4
    if d % 2 == 0
        g.x += (d - 1)
    else
        g.y -= (d - 2)
    end
end

function around(g::Grid)
    Channel() do c
        prev = moved_from(g)
        if prev[1] != g.x - 1 && g.x >= 2
            push!(c, (g.x - 1, g.y, 0))
        end
        if prev[1] != g.x + 1 && g.x < length(data)
            push!(c, (g.x + 1, g.y, 2))
        end
        if prev[2] != g.y - 1 && g.y >= 2
            push!(c, (g.x, g.y - 1, 3))
        end
        if prev[2] != g.y + 1 && g.y < length(data[g.x])
            push!(c, (g.x, g.y + 1, 1))
        end
    end
end

function step(g::Grid)
    cur = g[g.x][g.y]
    if cur == '+'
        g.direction = filter(n -> n[1] != ' ',
                             [(g[x][y], d) for (x, y, d) in around(g)])[1][2]
    elseif cur == '|' || cur == '-'
    else
        push!(g.letters, cur)
    end
    g.steps += 1
    move(g)
end

function follow(g::Grid)
    while g[g.x][g.y] != ' '
        step(g)
    end
    join(g.letters), g.steps
end

